import { Component } from '@angular/core';

@Component({
  selector: 'app-travels',
  templateUrl: './travels.component.html',
  styleUrls: ['./travels.component.css']
})

export class TravelsComponent {
  Travels: {Name:string; TravelImage:string; Description:string;}[];
  TravelsFiltered : {Name:string; TravelImage:string; Description:string;}[];
  constructor() {
    this.Travels=[{Name: "Paris",
    TravelImage: "data:image/jpeg;base64,",
    Description: "My vacation in Paris. September 13-20, 2021."},
    
    {Name: "Rome",
    TravelImage: "data:image/jpeg;base64,",
    Description: "My vacation in Rome. July 8-9, 2019."}];
    this.TravelsFiltered = this.Travels;
   }
  SearchTravel(value:string | null){
    if(value == null || value.trim()==''){
      this.TravelsFiltered = this.Travels;
    }
    this.TravelsFiltered = this.Travels.filter(function(t){
       return t.Name.toUpperCase().includes(value?.toString().toUpperCase()??"");
    });
  }
}
